        <div class="tabs" id="coverageTabs">
            <button id="btnOffice" tab="office">
                <i class="icon-office"></i>
                <div class="text">
                    Oifcinas y Bodegas
                </div>
            </button>
            <button id="btnUnit" class="active" tab="unit">
                <i class="icon-person"></i>
                <div class="text">
                    Unidades de servicio
                </div>
            </button>
            <button id="btnTruck" tab="truck">
                <i class="icon-truck"></i>
                <div class="text">
                    Flota terrestre
                </div>
            </button>
        </div>
