@section('extraJs')
<script>
$(function(){
	$('#coverageTabs button').on( 'click' , function() {
		$(this).addClass('active').siblings().removeClass('active');
		var tab = $(this).attr('tab');
		if ( tab == 'unit' ) {
			$('.base div.office').fadeOut();
			$('.base div.unit').fadeIn();
			$('.base div.truck').fadeOut();
			$('path.unit' ).removeClass('hidden');
			$.each( $('div.unit ul li') , function( key , val ) {
				var time = 1000
				time += key * 250
				animate( val , 'zoomIn' , time  )
				
			})

		}
		if ( tab == 'office' ) {
			$('.base div.office').fadeIn();
			$('.base div.unit').fadeOut();
			$('.base div.truck').fadeOut();
			$('path.unit').not('.office').addClass('hidden');
			$('path.office' ).removeClass('hidden');
			$.each( $('div.office ul li') , function( key , val ) {
				var time = 500
				time += key * 125
				animate( val , 'zoomIn' , time  )
				
			})
		}
		if ( tab == 'truck' ) {
			$('.base div.office').fadeOut();
			$('.base div.unit').fadeOut();
			$('.base div.truck').fadeIn();
			$('path.unit' ).addClass('hidden');
			$.each( $('div.truck .points div') , function( key , val ) {
				var time = 1000
				time += key * 125
				animate( val , 'bounceInDown' , time  )
				
			})
			animate( 'div.truck .legend' , 'lightSpeedIn' , 3000  )
		}
    } )
    $('#coverageTabs #btnUnit').click()
    // setTimeout( function() {
    //     $('.base div.office').fadeIn();
    //     $('.base div.unit').fadeOut();
    //     $('.base div.truck').fadeOut();
    //     $('path.unit').not('.office').addClass('hidden');
    //     $('path.office' ).removeClass('hidden');
    //     $.each( $('div.office ul li') , function( key , val ) {
    //         var time = 500
    //         time += key * 125
    //         animate( val , 'zoomIn' , time  )
            
    //     })
    // } , 250)
    function animate( el , cls , time) {
        setTimeout( function() {
            $( el ).removeClass('hidden').addClass(  'animated '+  cls )
        } , time )
    }
})
</script>
@endsection